import React, { useState } from 'react';

const VendorPanel = () => {
    const [products, setProducts] = useState([]);
    const [newProduct, setNewProduct] = useState('');

    const handleAddProduct = () => {
        if (newProduct.trim()) {
            setProducts([...products, newProduct]);
            setNewProduct('');
        }
    };

    const handleDeleteProduct = (index) => {
        setProducts(products.filter((_, i) => i !== index));
    };

    return (
        <div style={{ padding: '20px', maxWidth: '400px', margin: '0 auto' }}>
            <h2>Vendor Panel</h2>

            {/* List Products Button */}
            <button 
                style={{ marginBottom: '20px' }}
                onClick={() => alert(`Products: ${products.join(', ')}`)}
            >
                List Products
            </button>

            {/* Add Product */}
            <div style={{ marginBottom: '20px' }}>
                <input
                    type="text"
                    value={newProduct}
                    onChange={(e) => setNewProduct(e.target.value)}
                    placeholder="Enter product name"
                    style={{ marginRight: '10px', padding: '5px', width: '70%' }}
                />
                <button onClick={handleAddProduct} style={{ padding: '5px' }}>
                    Add Product
                </button>
            </div>

            {/* Display Products with Delete Option */}
            <ul>
                {products.map((product, index) => (
                    <li key={index} style={{ marginBottom: '10px' }}>
                        {product}
                        <button 
                            onClick={() => handleDeleteProduct(index)} 
                            style={{ marginLeft: '10px', padding: '3px 6px', color: 'white', background: 'red', border: 'none', cursor: 'pointer' }}
                        >
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default VendorPanel;
