import React, { useState } from 'react';
import './Vendor.css';

const VendorPanel = () => {
    const [products, setProducts] = useState([]);
    const [productName, setProductName] = useState('');
    const [productPrice, setProductPrice] = useState('');
    const [productDescription, setProductDescription] = useState('');

    const handleAddProduct = () => {
        if (productName.trim() && productPrice.trim()) {
            const newProduct = {
                name: productName,
                price: productPrice,
                description: productDescription
            };
            setProducts([...products, newProduct]);
            setProductName('');
            setProductPrice('');
            setProductDescription('');
        } else {
            alert('Please enter product name and price.');
        }
    };

    const handleDeleteProduct = (index) => {
        setProducts(products.filter((_, i) => i !== index));
    };

    return (
        <>
            <div className="header">
                <h1 className="logo">
                    Flex<span className="white-text">Fuels<p className="tagline">...a one-stop solution for optimum nutrition</p></span>
                    //<p className="tagline">...a one-stop solution for optimum nutrition</p>
                </h1>
                
            </div>

            <div className="vendor-container">
                <h2 className="vendor-title">Vendor Panel</h2>

                {/* Add Product Form */}
                <div className="add-product-form">
                    <input
                        type="text"
                        value={productName}
                        onChange={(e) => setProductName(e.target.value)}
                        placeholder="Product Name"
                        className="input-field"
                    />
                    <input
                        type="text"
                        value={productPrice}
                        onChange={(e) => setProductPrice(e.target.value)}
                        placeholder="Product Price"
                        className="input-field"
                    />
                    <input
                        type="text"
                        value={productDescription}
                        onChange={(e) => setProductDescription(e.target.value)}
                        placeholder="Product Description"
                        className="input-field"
                    />
                    <button onClick={handleAddProduct} className="add-button">
                        Add Product
                    </button>
                </div>

                {/* Display Products with Delete Option */}
                <ul className="product-list">
                    {products.map((product, index) => (
                        <li key={index} className="product-item">
                            <div className="product-info">
                                <strong>{product.name}</strong> - ${product.price}
                                <p>{product.description}</p>
                            </div>
                            <button 
                                onClick={() => handleDeleteProduct(index)} 
                                className="delete-button"
                            >
                                Delete
                            </button>
                        </li>
                    ))}
                </ul>
            </div>
        </>
    );
};

export default VendorPanel;
