import React, { useState } from 'react';
import './Vendor'

const VendorPanel = () => {
    const [products, setProducts] = useState([]);
    const [productName, setProductName] = useState('');
    const [productPrice, setProductPrice] = useState('');
    const [productDescription, setProductDescription] = useState('');

    const handleAddProduct = () => {
        if (productName.trim() && productPrice.trim()) {
            const newProduct = {
                name: productName,
                price: productPrice,
                description: productDescription
            };
            setProducts([...products, newProduct]);
            setProductName('');
            setProductPrice('');
            setProductDescription('');
        } else {
            alert('Please enter product name and price.');
        }
    };

    const handleDeleteProduct = (index) => {
        setProducts(products.filter((_, i) => i !== index));
    };

    return (
        <>
        <div className="header">
            <h1 className="logo">Flex<span className="white-text">Fuels</span><p className="tagline">...a one stop solution for optimum nutrition</p>
            </h1>
            </div>
        <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
            <h2>Vendor Panel</h2>

            {/* Add Product Form */}
            <div style={{ marginBottom: '20px' }}>
                <input
                    type="text"
                    value={productName}
                    onChange={(e) => setProductName(e.target.value)}
                    placeholder="Product Name"
                    style={{ marginRight: '10px', padding: '5px', width: '30%' }}
                />
                <input
                    type="text"
                    value={productPrice}
                    onChange={(e) => setProductPrice(e.target.value)}
                    placeholder="Product Price"
                    style={{ marginRight: '10px', padding: '5px', width: '20%' }}
                />
                <input
                    type="text"
                    value={productDescription}
                    onChange={(e) => setProductDescription(e.target.value)}
                    placeholder="Product Description"
                    style={{ marginRight: '10px', padding: '5px', width: '40%' }}
                />
                <button onClick={handleAddProduct} style={{ padding: '5px' }}>
                    Add Product
                </button>
            </div>

            {/* Display Products with Delete Option */}
            <ul>
                {products.map((product, index) => (
                    <li key={index} style={{ marginBottom: '10px' }}>
                        <strong>{product.name}</strong> - ${product.price}
                        <p>{product.description}</p>
                        <button 
                            onClick={() => handleDeleteProduct(index)} 
                            style={{ padding: '5px 10px', color: 'white', background: 'red', border: 'none', cursor: 'pointer' }}
                        >
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
        </div>
        </>
    );
};

export default VendorPanel;
