import React, { useState } from 'react';
import './AdminPanel.css';

const AdminPanel = () => {
    const [products, setProducts] = useState([
        { id: 1, name: 'Protein Powder', price: 29.99 },
        { id: 2, name: 'Yoga Mat', price: 19.99 }
    ]);
    const [users, setUsers] = useState([
        { id: 1, name: 'John Doe', email: 'john@example.com' },
        { id: 2, name: 'Jane Smith', email: 'jane@example.com' }
    ]);
    const [newProductName, setNewProductName] = useState('');
    const [newProductPrice, setNewProductPrice] = useState('');

    const handleAddProduct = () => {
        if (newProductName.trim() && newProductPrice.trim()) {
            const newProduct = {
                id: products.length + 1,
                name: newProductName,
                price: parseFloat(newProductPrice)
            };
            setProducts([...products, newProduct]);
            setNewProductName('');
            setNewProductPrice('');
        } else {
            alert('Please enter product name and price.');
        }
    };

    const handleRemoveProduct = (id) => {
        setProducts(products.filter(product => product.id !== id));
    };

    const handleRemoveUser = (id) => {
        setUsers(users.filter(user => user.id !== id));
    };

    return (
        <>
            <div className="header">
                <h1 className="logo">
                    Flex<span className="white-text">Fuels</span>
                    <p className="tagline">...a one-stop solution for optimum nutrition</p>
                </h1>
            </div>

            <div className="admin-container">
                <h2 className="admin-title">Admin Panel</h2>

                {/* Add Product Form */}
                <div className="add-product-form">
                    <input
                        type="text"
                        value={newProductName}
                        onChange={(e) => setNewProductName(e.target.value)}
                        placeholder="Product Name"
                        className="input-field"
                    />
                    <input
                        type="text"
                        value={newProductPrice}
                        onChange={(e) => setNewProductPrice(e.target.value)}
                        placeholder="Product Price"
                        className="input-field"
                    />
                    <button onClick={handleAddProduct} className="add-button">
                        Add Product
                    </button>
                </div>

                {/* Manage Products */}
                <div className="product-management">
                    <h3>Manage Products</h3>
                    <ul className="product-list">
                        {products.map(product => (
                            <li key={product.id} className="product-item">
                                <div className="product-info">
                                    <strong>{product.name}</strong> - ₹{product.price}
                                </div>
                                <button
                                    onClick={() => handleRemoveProduct(product.id)}
                                    className="remove-button"
                                >
                                    Remove
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>

                {/* Manage Users */}
                <div className="user-management">
                    <h3>Manage Users</h3>
                    <ul className="user-list">
                        {users.map(user => (
                            <li key={user.id} className="user-item">
                                <div className="user-info">
                                    <strong>{user.name}</strong> - {user.email}
                                </div>
                                <button
                                    onClick={() => handleRemoveUser(user.id)}
                                    className="remove-button"
                                >
                                    Remove
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </>
    );
};

export default AdminPanel;
